mapper.insert:
insert into t_menu(
update_time,create_time,remark,version, url,parent_id,name,position,
) values(
#{updateTime},#{createTime},#{remark},#{version}, #{url},#{parentId},#{name},#{position},
)

mapper.select:
select  
from  t_menu t 
where t.id = #{id}

		select  ,m1.name as  parentName
		from  t_menu t
		left join t_menu m1
		on t.parent_id=m1.id



				AND t.id = #{id}



				AND t.update_time = #{updateTime}



				AND t.create_time = #{createTime}



				AND t.remark = #{remark}



				AND t.version = #{version}



				AND t.url = #{url}



				AND t.parent_id = #{parentId}



				AND t.name = #{name}



				AND t.name = concat('%',#{name},'%')



				AND t.position = #{position}


				and t.create_time>#{startCreateTime}


				and t.create_time<=#{endCreateTime}





			order by t.${sortName}

                 ${sortType}

select ,
p.id as pId,p.menu_id,p.permission_name,p.method_name
from  t_menu t
left join t_permission p
on t.id=p.menu_id


	 	AND t.parent_id=#{parentId}


		AND t.menu_id = #{menuId}


		AND t.id = #{id}


order by t.position asc
